import Block from "./components/block/Block";
import Header from "./components/header/Header";
import "./app.scss"

const APICall = async() =>{
  const link ="https://data.covid19india.org/data.json";
  const res = await fetch(link);
  const data = await res.json();
//  console.log(data.statewise[0].state);
const apiData = {
  state: data.statewise[0].state ,
  recovered: data.statewise[0].recovered,
  confirmed: data.statewise[0].confirmed,
  daths:  data.statewise[0].deaths,
  active: data.statewise[0].active,
  updated: data.statewise[0].lastupdatedtime
}
  return(
    <>
     <div className="container"  >
    <Block color="#E0C097" p="OUR" head="COUNTRY" data={apiData.state}/>
    <Block color="#BD1616" p="TOTAL" head="RECOVERED" data={apiData.recovered}/>
    <Block color="#BD4B4B" p="TOTAL" head="CONFIRMED" data={apiData.confirmed}/>
    <Block color="#0A81AB" p="TOTAL" head="DEATHS" data={apiData.deaths}/>
    <Block color="#BA135D" p="TOTAL" head="ACTIVE" data={apiData.active}/>
    <Block color="#D8AC9C" p="LAST" head="UPDATED" data={apiData.updated}/>
    </div>

    </>

  );

}

function App() {

  return (
    <div className="app">

    <Header />
    {APICall()}

    </div>
  );
}

export default App;
